INSERT INTO products_movies(id, date_created, is_new_product, product_image_url, description, product_name, product_price, product_type, carrier, genre, language)
VALUES (1, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81tZmQwn2KL._SL1500_.jpg', 'From Damien Chazelle, BABYLON is an original epic set in 1920s Los Angeles led by Brad Pitt, Margot Robbie and Diego Calva, with an ensemble cast including Jovan Adepo, Li Jun Li and Jean Smart. A tale of outsized ambition and outrageous excess, it traces the rise and fall of multiple characters during an era of unbridled decadence and depravity in early Hollywood.', 'BABYLON', 19.25, 'MOVIE', 'DVD', 'Drama', 'English'),
(2, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/51so7TKBwUL._SX300_SY300_QL70_FMwebp_.jpg', 'A landmark film from Quentin Tarantino, Pulp Fiction is a gritty, intertwining crime story that redefined cinema in the 1990s. Known for its sharp dialogue and eclectic soundtrack, it stars John Travolta, Uma Thurman, and Samuel L. Jackson.', 'Pulp Fiction', 11.99, 'MOVIE', 'BluRay', 'Crime, Drama', 'English'),
(3, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91CSj6snB+L._SL1500_.jpg', 'Christopher Nolan\'s The Dark Knight is a riveting crime drama that pits Batman against his greatest foe, the Joker. With a stellar cast led by Christian Bale and Heath Ledger, this film redefined the superhero genre.', 'The Dark Knight', 14.99, 'MOVIE', 'DVD', 'Action, Crime, Drama', 'English'),
(4, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/714Kvax13CL._SL1500_.jpg', 'Forrest Gump, directed by Robert Zemeckis, is a heartwarming tale of an unlikely hero whose simple innocence and extraordinary experiences touch countless lives. Tom Hanks delivers an iconic performance in this beloved classic.', 'Forrest Gump', 9.99, 'MOVIE', 'BluRay', 'Drama, Romance', 'English'),
(5, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/61CkZlbKdwL._SX385_.jpg', 'Inception, directed by Christopher Nolan, is a mind-bending thriller that dives into the world of dreams. Leonardo DiCaprio leads an ensemble cast in this visually stunning and intellectually stimulating adventure.', 'Inception', 13.99, 'MOVIE', 'DVD', 'Action, Adventure, Sci-Fi', 'English'),
(6, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/810ODVWvc2S._SL1500_.jpg', 'The Shawshank Redemption is a timeless story of hope and friendship, set in a harsh prison environment. Directed by Frank Darabont and starring Tim Robbins and Morgan Freeman, it\'s an enduring favorite among film lovers.', 'The Shawshank Redemption', 12.50, 'MOVIE', 'BluRay', 'Drama', 'English'),
(7, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/5138soypHsL._SY445_.jpg', 'David Fincher\'s Fight Club is a dark, subversive exploration of modern masculinity and identity. Starring Brad Pitt and Edward Norton, this film challenges societal norms and remains a cult favorite.', 'Fight Club', 11.50, 'MOVIE', 'DVD', 'Drama', 'English'),
(8, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/61XTg+cjTbL._SL1500_.jpg', 'The Matrix, directed by the Wachowskis, revolutionized the sci-fi genre with its groundbreaking special effects and philosophical storytelling. Keanu Reeves stars as Neo, a hacker who discovers the true nature of reality.', 'The Matrix', 12.00, 'MOVIE', 'BluRay', 'Action, Sci-Fi', 'English'),
(9, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/51QN9MTF5YL._SX300_SY300_QL70_FMwebp_.jpg', 'Goodfellas, directed by Martin Scorsese, is a gritty, realistic portrayal of life in the mob. With a powerhouse cast led by Robert De Niro, Ray Liotta, and Joe Pesci, it\'s a must-see for crime drama enthusiasts.', 'Goodfellas', 10.99, 'MOVIE', 'DVD', 'Biography, Crime, Drama', 'English'),
(10, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/71xQm640mrL._SL1500_.jpg', 'Schindler\'s List, directed by Steven Spielberg, is a harrowing and poignant account of the Holocaust. Liam Neeson stars as Oskar Schindler, a man who saved over a thousand Jewish lives. This film is both devastating and uplifting.', 'Schindler\'s List', 13.50, 'MOVIE', 'BluRay', 'Biography, Drama, History', 'English'),
(11, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91oXnQjg+8L._SY445_.jpg', 'The Lord of the Rings: The Return of the King, directed by Peter Jackson, is the epic conclusion to the legendary fantasy trilogy. With breathtaking visuals and an unforgettable story, it remains a cinematic milestone.', 'The Lord of the Rings: The Return of the King', 14.00, 'MOVIE', 'DVD', 'Action, Adventure, Drama', 'English'),
(12, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81lJkDOq4IL._SY445_.jpg', 'Star Wars: Episode IV - A New Hope, directed by George Lucas, is the groundbreaking space opera that started it all. Featuring iconic characters and thrilling adventures, it laid the foundation for one of the most beloved franchises in film history.', 'Star Wars: Episode IV - A New Hope', 13.00, 'MOVIE', 'BluRay', 'Action, Adventure, Fantasy', 'English'),
(13, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/A1JVqNMI7UL._SL1500_.jpg', 'Christopher Nolan\'s Interstellar is a visually stunning and emotionally gripping journey through space and time. Starring Matthew McConaughey and Anne Hathaway, it explores themes of love, sacrifice, and human endurance.', 'Interstellar', 12.99, 'MOVIE', 'DVD', 'Adventure, Drama, Sci-Fi', 'English'),
(14, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91j7Fx-luwL._SL1500_.jpg', 'Ridley Scott\'s Gladiator is a sweeping historical epic that follows the journey of a betrayed Roman general, played by Russell Crowe, who seeks vengeance against the corrupt emperor. It\'s a tale of honor, revenge, and redemption.', 'Gladiator', 11.99, 'MOVIE', 'BluRay', 'Action, Adventure, Drama', 'English'),
(15, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81Tj46I+86L._SL1500_.jpg', 'The Silence of the Lambs, directed by Jonathan Demme, is a chilling psychological thriller featuring Anthony Hopkins as the infamous Hannibal Lecter and Jodie Foster as the determined FBI agent Clarice Starling.', 'The Silence of the Lambs', 10.99, 'MOVIE', 'DVD', 'Crime, Drama, Thriller', 'English'),
(16, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91hPM8H3Y7L._SL1500_.jpg', 'Se7en, directed by David Fincher, is a dark and gripping mystery thriller that follows two detectives, played by Brad Pitt and Morgan Freeman, as they hunt a serial killer who uses the seven deadly sins as his modus operandi.', 'Se7en', 12.50, 'MOVIE', 'BluRay', 'Crime, Drama, Mystery', 'English'),
(17, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/61UZ+Dq1OYL._SL1000_.jpg', 'Steven Spielberg\'s Saving Private Ryan is a powerful and realistic depiction of the horrors of World War II. Starring Tom Hanks, it follows a group of soldiers on a mission to rescue a paratrooper behind enemy lines.', 'Saving Private Ryan', 13.50, 'MOVIE', 'DVD', 'Drama, War', 'English'),
(18, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/814NcxBidXL._SL1500_.jpg', 'Jurassic Park, directed by Steven Spielberg, brings dinosaurs to life with groundbreaking special effects and thrilling storytelling. This adventure film, based on Michael Crichton\'s novel, captivates audiences of all ages.', 'Jurassic Park', 11.99, 'MOVIE', 'BluRay', 'Adventure, Sci-Fi, Thriller', 'English'),
(19, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/611YNf+ZWKL._SL1061_.jpg', 'The Lion King is a beloved Disney animated classic that tells the coming-of-age story of Simba, a lion cub destined to be king. Featuring memorable music and stunning animation, it\'s a timeless tale for all ages.', 'The Lion King', 10.50, 'MOVIE', 'DVD', 'Animation, Adventure, Drama', 'English'),
(20, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81RgJn7pZXL._SL1500_.jpg', 'James Cameron\'s Titanic is an epic romance and disaster film that tells the tragic story of the ill-fated RMS Titanic. Starring Leonardo DiCaprio and Kate Winslet, it combines historical events with a poignant love story.', 'Titanic', 13.00, 'MOVIE', 'BluRay', 'Drama, Romance', 'English'),
(21, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91XLwl9kE8L._SL1500_.jpg', 'The Departed, directed by Martin Scorsese, is a tense and gripping crime thriller set in Boston. Featuring Leonardo DiCaprio, Matt Damon, and Jack Nicholson, it explores themes of identity and deception within the world of crime and law enforcement.', 'The Departed', 12.99, 'MOVIE', 'DVD', 'Crime, Drama, Thriller', 'English'),
(22, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/51xy0NhUlHL.jpg', 'The Green Mile, directed by Frank Darabont, is a deeply moving drama set on Death Row. Starring Tom Hanks and Michael Clarke Duncan, it tells the story of a miraculous inmate with a mysterious gift.', 'The Green Mile', 11.50, 'MOVIE', 'BluRay', 'Crime, Drama, Fantasy', 'English'),
(23, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91j1XUW793L._SL1500_.jpg', 'Mel Gibson\'s Braveheart is an epic tale of the legendary Scottish hero William Wallace, who leads a rebellion against English tyranny. Known for its grand battle scenes and powerful storytelling, it\'s a historical drama of courage and freedom.', 'Braveheart', 12.00, 'MOVIE', 'DVD', 'Biography, Drama, History', 'English'),
(24, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/71IMR5HZv5L._AC_SL1500_.jpg', 'James Cameron\'s Avatar is a groundbreaking sci-fi epic that transports audiences to the alien world of Pandora. With stunning visuals and an engaging story, it follows the journey of a human soldier who integrates with the native Na\'vi people.', 'Avatar', 14.99, 'MOVIE', 'BluRay', 'Action, Adventure, Fantasy', 'English'),
(25, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81xvrrEXhXL._SL1500_.jpg', 'The Avengers, directed by Joss Whedon, is a thrilling superhero ensemble film that brings together characters from the Marvel Universe. Featuring Iron Man, Thor, Hulk, and more, it\'s a high-energy action movie filled with humor and excitement.', 'The Avengers', 13.99, 'MOVIE', 'DVD', 'Action, Adventure, Sci-Fi', 'English'),
(26, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/81tCjSpblaL._SL1500_.jpg', 'The Social Network, directed by David Fincher, tells the gripping story of the creation of Facebook. Starring Jesse Eisenberg as Mark Zuckerberg, it\'s a tale of ambition, betrayal, and the complex dynamics of the tech world.', 'The Social Network', 11.00, 'MOVIE', 'BluRay', 'Biography, Drama', 'English'),
(27, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/7112Y87kaFL._SL1360_.jpg', 'A Beautiful Mind, directed by Ron Howard, is a biographical drama about the life of mathematician John Nash. Starring Russell Crowe, it explores his brilliance, struggles with mental illness, and eventual triumph.', 'A Beautiful Mind', 10.99, 'MOVIE', 'DVD', 'Biography, Drama', 'English'),
(28, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/71tszqSo9VL._SL1500_.jpg', 'Django Unchained, directed by Quentin Tarantino, is a stylish and gritty western that follows a freed slave, played by Jamie Foxx, on a quest to rescue his wife. It features intense performances and sharp dialogue.', 'Django Unchained', 12.50, 'MOVIE', 'BluRay', 'Drama, Western', 'English'),
(29, '2024-06-11', '1', 'https://m.media-amazon.com/images/I/91m2MB2lYFL._SL1500_.jpg', 'The Wolf of Wall Street, directed by Martin Scorsese, is a wild and darkly comedic look at the life of stockbroker Jordan Belfort. Starring Leonardo DiCaprio, it showcases the excesses and corruption of Wall Street.', 'The Wolf of Wall Street', 11.99, 'MOVIE', 'DVD', 'Biography, Crime, Drama', 'English');

